mapper.select:

	SELECT no, title, content, hit
	FROM board
	WHERE no LIKE #{no}

mapper.update:

	UPDATE board
	SET title = #{title}
	   ,content = #{content}
	WHERE no LIKE #{no}

mapper.select:

	SELECT title, content
	FROM board
	WHERE no LIKE #{no}

mapper.delete:

	DELETE
	FROM board
	WHERE no LIKE #{no}

mapper.update:

	UPDATE board
	SET hit = NVL(hit,0) + 1
	WHERE no LIKE #{no}
